INSERT INTO `guestbook` VALUES ('444', 'yunita suryanti', 'yunitasuryanti@yahoo.com', 'menyambung pertanyaan sebelumnya pak. ayah saya di surat nikahnya bernama Kasmiran rahmana soesanto dan ibu saya Patonah, tetapi di Ijasah saya nama ayah Kasmiran (bukan nama lengkap) dan ibu di KTP siti fatonah (bukan dengan ejaan lama patonah), saya khawatir jauh-jauh dari sidoarjo (jawa timur)ke Bandung di tolak gara2 hal tersebut. mohon petunjuk pak. terimakasih', '2013-07-04 02:44:06', '');
